        private readonly ITestOutputHelper _output;

        private class DeliveryInstructions
        {
            private const char Up = '(';
            private const char Down = ')';
            private readonly string _map;

            /// <summary>
            /// Returns the floor on which the delivery should be made based on the directions specified in the map.
            /// </summary>
            public int DestinationFloor => (_map?.Count(c => c == Up) ?? 0) - (_map?.Count(c => c == Down) ?? 0);
            
            /// <summary>
            /// Determines which character in the map, i.e. which part of the instruction, led to going into the basement.
            /// </summary>
            public int EnterBasementOnInstructionN
            {
                get
                {
                    int currentFloor = 0;
                    int characterPosition = 1;

                    foreach (var movement in _map)
                    {
                        currentFloor += movement switch
                        {
                            Up => 1,
                            Down => -1,
                            _ => throw new InvalidOperationException($"Character {movement} not expected")
                        };

                        if (currentFloor < 0)
                            break;

                        characterPosition++;
                    }

                    return characterPosition;
                }
            }

            public DeliveryInstructions(string map)
            {
                _map = map;
            }
        }

        public D1(ITestOutputHelper output)
        {
            _output = output;
        }

        [Theory]
        [InlineData("(())", 0)]
        [InlineData("()()", 0)]
        [InlineData("(((", 3)]
        [InlineData("(()(()(", 3)]
        [InlineData("())", -1)]
        [InlineData(")))", -3)]
        [InlineData(")())())", -3)]
        public void Part_1_Example(string map, int expectedDestinationFloor)
        {
            var instructions = new DeliveryInstructions(map);
            Assert.Equal(expectedDestinationFloor, instructions.DestinationFloor);
        }

        /// <summary>
        /// Find the floor the input string points to.
        /// Start at position 0. 
        /// Open bracket represents going up one floor. 
        /// Closing bracket represents going down one floor. 
        /// End position is 0 + {count of '('} - {count of '}'}.
        /// </summary>
        /// <param name="input">The input that contains the number of floors going up and down</param>
        [Fact]
        public void Part_1()
        {
            var instructions = new DeliveryInstructions(Input);

            _output.WriteLine(instructions.DestinationFloor);
        }

        [Theory]
        [InlineData(")", 1)]
        [InlineData("()())", 5)]
        public void Part_2_Example(string map, int expectedBasementPosition)
        {
            var instructions = new DeliveryInstructions(map);

            Assert.Equal(expectedBasementPosition, instructions.EnterBasementOnInstructionN);
        }

        /// <summary>
        /// Find the position of the character that lead to a basement floor (negative number).
        /// Start at position 0. 
        /// Open bracket represents going up one floor. 
        /// Closing bracket represents going down one floor. 
        /// End position is 0 + {count of '('} - {count of '}'}.
        /// </summary>
        /// <remarks>
        /// The position of characters <b>does not</b> use index values, so the first character is at position 1.
        /// </remarks>
        [Fact]
        public void Part_2()
        {

            var instructions = new DeliveryInstructions(Input);

            _output.WriteLine(instructions.EnterBasementOnInstructionN);
        }
