        private readonly ITestOutputHelper _output;

        public D1_NotQuiteLisp(ITestOutputHelper output)
        {
            _output = output;
        }

        /// <summary>
        /// Find the floor the input string points to.
        /// Start at position 0. 
        /// Open bracket represents going up one floor. 
        /// Closing bracket represents going down one floor. 
        /// End position is 0 + {count of '('} - {count of '}'}.
        /// </summary>
        /// <param name="input">The input that contains the number of floors going up and down</param>
        [Theory]
        [InlineData(Input)]
        public void Part_1(string input)
        {
            int start = 0;
            int ups = input?.Count(c => c == Up) ?? 0;
            int downs = input?.Count(c => c == Down) ?? 0;

            var end = start + ups - downs;

            _output.WriteLine(end);
        }

        /// <summary>
        /// Find the position of the character that lead to a basement floor (negative number).
        /// Start at position 0. 
        /// Open bracket represents going up one floor. 
        /// Closing bracket represents going down one floor. 
        /// End position is 0 + {count of '('} - {count of '}'}.
        /// </summary>
        /// <remarks>
        /// The position of characters <b>does not</b> use index values, so the first character is at position 1.
        /// </remarks>
        /// <param name="input"></param>
        [Theory]
        [InlineData(Input)]
        public void Part_2(string input)
        {
            int currentFloor = 0;
            int characterPosition = 1;

            foreach(var movement in input)
            {
                currentFloor += movement switch
                {
                    Up => 1, 
                    Down => -1, 
                    _ => throw new InvalidOperationException($"Character {movement} not expected")
                };

                if (currentFloor < 0)
                    break;

                characterPosition++;
            }

            _output.WriteLine(characterPosition);
        }
    }
